WHILE (@i <= (SELECT MAX(ID_ACM_GROUPE) FROM ACM_GROUPE)) 
BEGIN
set @value = isNull((select id_acm_groupe from ACM_GROUPE where ID_ACM_GROUPE = @i),0);
if (@value != 0) 
BEGIN
 INSERT INTO [dbo].[ACM_IHM_FIELD_GROUPE]([ID_GROUPE],[ID_ACM_IHM_FIELD],[HABILITATION],[ACM_ENABLED],[DATE_INSERTION],[INSERT_BY],[ACM_VERSION])
 select @value,ID,'ENABLE',1,GETDATE(),'ADMIN',0 from ACM_IHM_FIELD
 SET @i = @i + 1
 end;
else
SET @i = @i + 1;
end
 